import UserService from "../services/user.service";

const RecipeUser = () => {
  const [content, setContent] = useState([]);


  useEffect(() => {
    UserService.getUserRecipe().then(response => {
        response.status === 401 ? setContent(response.status) : setContent(response.data);
      }).then(
      (response) => {
          console.log(2, response)
